import EllipsisMiddle from "components/EllipsisMiddle";
import { Space, Button, Image } from "antd";
import { useCallback } from "react";
import { useHistory } from "react-router-dom";
import ImgLightning from "img/lightning.png";
export default function AssetItem({ item }) {
  const history = useHistory();
  const onDirectToReceive = useCallback(
    (assetId) => {
      history.push(`/wallet/receive/${assetId}`);
    },
    [history]
  );
  const onDirectToSend = useCallback(
    (assetId) => {
      history.push(`/wallet/send/${assetId}`);
    },
    [history]
  );
  return (
    <>
      <div className="nostr-asset">
        <Image
          className="nostr-asset-logo"
          src={ImgLightning}
          preview={false}
          fallback="data:image/png;base64,"
        />
        <div className="nostr-asset-info">
          <div className="nostr-asset-info-item">
            <span className="nostr-asset-info-item__title">Asset Name</span>
            <span className="nostr-asset-info-item__value">{item?.name}</span>
          </div>
          <div className="nostr-asset-info-item">
            <span className="nostr-asset-info-item__title">Balance</span>
            <span className="nostr-asset-info-item__value">{item?.amount || 0}</span>
          </div>
          <div className="nostr-asset-info-item">
            <span className="nostr-asset-info-item__title">Asset ID</span>
            <span className="nostr-asset-info-item__value">
              <EllipsisMiddle suffixCount={10}>{item?.assetId}</EllipsisMiddle>
            </span>
          </div>
        </div>

        <div className="nostr-asset-actions">
          <Space>
            <Button
              type="primary"
              onClick={() => {
                onDirectToReceive(item?.assetId);
              }}
            >
              Receive
            </Button>
            <Button
              type="primary"
              disabled={item?.amount === 0}
              onClick={() => {
                onDirectToSend(item?.assetId);
              }}
            >
              Send
            </Button>
          </Space>
        </div>
      </div>
    </>
  );
}
